import ExpenseItem from "./ExpenseItem";
import Card from "../shared/Card";
import './Expenses.css';
import ExpensesFilter from "./ExpensesFilter";
import {useState} from "react";

function Expenses(props) {

    const [year, setYear] = useState(2019);

    let displayItems = <p>No expenses for given year(s)</p>;

    const filteredItems = props.expenseData.filter(item => {
        return item.date.getFullYear().toString() === year;
    });

    displayItems = !filteredItems.length === 0 && filteredItems.map(item => {
        <ExpenseItem item={item}/>
        })


    function filterYearHandler(year) {
        setYear(year);
        console.log(year);
    }

    return (
        <Card className="expenses">
            <ExpensesFilter selected={year} onFilterYear={filterYearHandler} />
            {displayItems}
        </Card>
    )
}

export default Expenses;
